<?php/*****************************************************************************************	H selida auth parexei tis epiloges gia diagrafh 'h kai pros8hkh kapoias paroxhs*****************************************************************************************///including required filesinclude('includes.php');//kataxwroume thn SESSION metavlhth newFac se mia topikh metavlhth $newFacif(isset($_POST['newFac'])) $newFac=$_POST['newFac']; else $newFac=NULL;//kataxwroume thn SESSION metavlhth facility se mia topikh metavlhth $facilityif(isset($_POST['facility'])) $delFac=$_POST['facility']; else $delFac=NULL;try{	//elegxoume an o xrhsths einai swsta sundedemenos	check_valid_user();	//elegxoume an to checkbox gia thn diagrafh paroxwn einai markarismeno	if(filledOut($delFac))	{		dispHeader('Diagrafh paroxwn');		//an nai diagrafoume mia mia tis paroxes		foreach($delFac as $fac)		{			echo "$fac <br />";			db_del_fac($fac);		}		dispFooter();	}	//elegxoume an o xrhsths evale mia kainourgia paroxh	else if(filledOut($newFac))	{		//an nai eisagoume thn paroxh sthn vash		$newFac=$_POST['newFac'];		//me auth thn sunarthsh eisagoume thn paroxh sth vash		db_insert1('facilities','facility',"'$newFac'");		dispHeader("Kainourgia paroxh $newFac");		//H sunarthsh dispFacilitiesSettings() emfanizei to menu epilogwn gia thn diagrafh 'h kai pros8hkh paroxwn		dispFacilitiesSettings();		dispFooter();	}	//an den einai tipota epilegmeno tote apla emfanizoume to menu epilogwn	else	{		dispHeader('Oi uparxouses paroxes');		//H sunarthsh dispFacilitiesSettings() emfanizei to menu epilogwn gia thn diagrafh 'h kai pros8hkh paroxwn		dispFacilitiesSettings();		dispFooter();	}}catch(Exception $e){	dispHeader("Error:");	echo $e->getMessage();	dispFooter();	exit;}      ?>